import React from "react";

const Textarea = ({
  name,
  label,
  value,
  onChange,
  placeholder,
  row = 3,
  className,
  help,
  helpValue
}) => {
  return (
    <div className="field">
      <label className="label">{label}</label>
      <div className="control">
        <textarea
          name={name}
          className={"textarea " + className}
          placeholder={placeholder}
          value={value}
          onChange={onChange}
          rows={row}
        />
      </div>
      {help && <p className={"help " + className}>{helpValue}</p>}
    </div>
  );
};

export default Textarea;
